const Fs = require('fs');
const Pdf = require('./lib/pdf-parse.js');

module.exports = Pdf;

// Only enable debug/demo behavior when this module is run directly
// and the environment variable `PDF_PARSE_RUN_TEST` is explicitly set to '1'.
let isDebugMode = !module.parent && process.env.PDF_PARSE_RUN_TEST === '1';

//process.env.AUTO_KENT_DEBUG


//for testing purpose
if (isDebugMode) {
    // Running demo/test code - read a sample PDF and write parsed text.
    // This block is intentionally only executed when `PDF_PARSE_RUN_TEST=1`.
    let PDF_FILE = './test/data/05-versions-space.pdf';
    let dataBuffer = Fs.readFileSync(PDF_FILE);
    Pdf(dataBuffer).then(function(data) {
        Fs.writeFileSync(`${PDF_FILE}.txt`, data.text, {
            encoding: 'utf8',
            flag: 'w'
        });
        debugger;
    }).catch(function(err) {
        debugger;
    });

}
